// Extract the weather data from the API response
const weatherData = $input.first().json;
const forecastHours = weatherData.forecastHours;

// Get current hour (first item in the array)
const current = forecastHours[0];

// Get the next few hours for trends
const next12Hours = forecastHours.slice(0, 13);

// Find day and evening forecasts
const dayForecasts = forecastHours.filter(h => h.isDaytime);
const eveningForecasts = forecastHours.filter(h => !h.isDaytime);

// Calculate high/low for the day
const dayTemps = dayForecasts.map(h => h.temperature.degrees);
const highTemp = Math.max(...dayTemps);
const lowTemp = Math.min(...dayTemps);

// Check for rain chances
const rainChances = forecastHours.map(h => h.precipitation.probability.percent);
const maxRainChance = Math.max(...rainChances);

// Format a readable summary
const formattedData = {
  currentConditions: {
    time: `${current.displayDateTime.hours}:00`,
    temperature: current.temperature.degrees,
    feelsLike: current.feelsLikeTemperature.degrees,
    condition: current.weatherCondition.description.text,
    humidity: current.relativeHumidity,
    windSpeed: current.wind.speed.value,
    windDirection: current.wind.direction.cardinal,
    uvIndex: current.uvIndex,
    rainChance: current.precipitation.probability.percent
  },

  dayOverview: {
    highTemp: highTemp,
    lowTemp: lowTemp,
    maxRainChance: maxRainChance,
    summaryConditions: [...new Set(dayForecasts.map(h => h.weatherCondition.description.text))].join(', ')
  },

  next12Hours: next12Hours.map(h => ({
    time: `${h.displayDateTime.hours}:00`,
    temp: h.temperature.degrees,
    condition: h.weatherCondition.description.text,
    rainChance: h.precipitation.probability.percent
  })),

  // Formatted text for AI prompt
  promptText: `Today's Weather Summary:

CURRENT CONDITIONS (${current.displayDateTime.hours}:00):
- Temperature: ${current.temperature.degrees}Â°C (feels like ${current.feelsLikeTemperature.degrees}Â°C)
- Conditions: ${current.weatherCondition.description.text}
- Humidity: ${current.relativeHumidity}%
- Wind: ${current.wind.speed.value} km/h from the ${current.wind.direction.cardinal.toLowerCase().replace(/_/g, '-')}
- UV Index: ${current.uvIndex}
- Chance of rain: ${current.precipitation.probability.percent}%

TODAY'S FORECAST:
- High: ${highTemp}Â°C
- Low: ${lowTemp}Â°C
- Conditions throughout day: ${[...new Set(dayForecasts.map(h => h.weatherCondition.description.text))].join(', ')}
- Maximum rain chance: ${maxRainChance}%

NEXT 12 HOURS:
${next12Hours.map(h => 
