const workflowId = $input.item.json.same.id;
const allDecoded = $('Decode to json').all();

const matched = allDecoded.find(item => item.json.id === workflowId);

return matched 
  ? { json: { dataset: matched.json } }
  : {};

const githubWorkflows = $('If n8n before GitHub').all().map(item => item.json);
const n8nWorkflows = $('n8n').all().map(item => item.json);

const githubIds = new Set(githubWorkflows.map(item => item.same.id));
const matches = n8nWorkflows.filter(item => githubIds.has(item.id));

return matches.map(item => ({ json: item }));

